import React, { useRef, useEffect } from 'react';
import * as d3 from 'd3';
import terra from '../../../../assets/StatusReport/terra.png';
import injective from '../../../../assets/StatusReport/injective.png';

const LiquidityPerDeployment = () => {
  const chartRef = useRef();

  useEffect(() => {
    const data = [
      { chainId: 'phoenix-1', value: 0.4, color: '#534AF9' },
      { chainId: 'injective-1', value: 0.6, color: '#83FFCB' },
    ];

    const width = 300;
    const height = 300;
    const radius = Math.min(width, height) / 2;

    const svg = d3
      .select(chartRef.current)
      .attr('width', width)
      .attr('height', height)
      .append('g')
      .attr('transform', `translate(${width / 2}, ${height / 2})`);

    const arc = d3.arc().innerRadius(radius * 0.75).outerRadius(radius);

    const pie = d3
      .pie()
      .startAngle(-1 * Math.PI / 3)
      .endAngle(5 * Math.PI / 3)
      .sort(null)
      .value((d) => d.value);

    const arcs = svg
      .selectAll('.arc')
      .data(pie(data))
      .enter()
      .append('g')
      .attr('class', 'arc');

    arcs
      .append('path')
      .attr('d', arc)
      .attr('fill', (d) => d.data.color)
      .on('mouseover', function (d) {
        d3.select(this).attr('fill', d3.color(d.data.color).brighter(0.5));
      })
      .on('mouseout', function (d) {
        d3.select(this).attr('fill', d.data.color);
      });

    arcs
      .append('line')
      .attr('x1', (d) => arc.centroid(d)[0] * 0.8)
      .attr('y1', (d) => arc.centroid(d)[1] * 0.8)
      .attr('x2', (d) => arc.centroid(d)[0] * 1.25)
      .attr('y2', (d) => arc.centroid(d)[1] * 1.25)
      .attr('stroke', (d) => d.data.color);

    arcs
      .append('text')
      .attr('x', (d) => arc.centroid(d)[0] * 1.4)
      .attr('y', (d) => arc.centroid(d)[1] * 1.4)
      .attr('text-anchor', 'middle')
      .text((d) => `${Math.round(d.data.value * 100)}M`)
      .attr('fill', 'white')
      .style('font-size', '14px');

    const logoImages = {
      'phoenix-1': terra,
      'injective-1': injective,
    };

    arcs
      .append('image')
      .attr('xlink:href', (d) => logoImages[d.data.chainId])
      .attr('x', (d) => arc.centroid(d)[0] * 1.25 - 15)
      .attr('y', (d) => arc.centroid(d)[1] * 1.25 - 15)
      .attr('width', 30)
      .attr('height', 30)

      svg
      .append('text')
      .attr('x', 0)
      .attr('y', -radius * 0.25)
      .attr('text-anchor', 'middle')
      .text('Total TVL')
      .style('font-size', '18px');

    svg
      .append('text')
      .attr('x', 0)
      .attr('y', radius * 0.25)
      .attr('text-anchor', 'middle')
      .text('Astroport')
      .style('font-size', '24px');
  }, []);

  return <svg ref={chartRef}></svg>;
};

export default LiquidityPerDeployment;
