import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import Chart from 'chart.js/auto';
import { Doughnut } from 'react-chartjs-2';

const LiquidityPerDeployment = () => {
  const [ltvData, setLtvData] = useState([]);

  const chartRef = useRef(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:5001/api/data');
        const data = response?.data?.ltv_data;
        if (data) {
          setLtvData(JSON.parse(data));
        }
      } catch (error) {
        console.error(error);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    if (chartRef.current && ltvData?.length) {
      const chartData = {
        labels: ltvData.map(chain => chain.chainId),
        datasets: [
          {
            data: ltvData.map(chain => chain.totalLiquidityUsd),
            backgroundColor: ltvData.map(chain =>
              chain.chainId === 'injective-1' ? '#FF6384' : '#36A2EB'
            ),
          },
        ],
      };

      const chartOptions = {
        responsive: true,
        maintainAspectRatio: false,
      };

      const chartInstance = new Chart(chartRef.current, {
        type: 'doughnut',
        data: chartData,
        options: chartOptions,
      });

      return () => {
        chartInstance.destroy();
      };
    }
  }, [ltvData]);

  return (
    <div>
      <h3>Liquidity per Deployment</h3>
      <p>This is a sample Liquidity Per Deployment component.</p>
      <Doughnut ref={chartRef} />
    </div>
  );
};

export default LiquidityPerDeployment;
