import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import * as d3 from 'd3';

const LiquidityPerDeployment = () => {
  const [ltvData, setLtvData] = useState([]);
  const chartRef = useRef(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:5001/api/data');
        const data = response?.data?.ltv_data;
        if (data) {
          setLtvData(JSON.parse(data));
        }
      } catch (error) {
        console.error(error);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    if (chartRef.current) {
      const svg = d3.select(chartRef.current);
      const width = +svg.attr('width');
      const height = +svg.attr('height');
      const radius = Math.min(width, height) / 2;
      const arcWidth = radius / 5;

      const arc = d3
        .arc()
        .outerRadius(radius - arcWidth)
        .innerRadius(radius - arcWidth * 2)
        .startAngle((d) => d.startAngle)
        .endAngle((d) => d.endAngle)
        .padAngle(0.02)
        .cornerRadius(5);

      const totalLiquidity = ltvData.reduce((acc, chain) => acc + chain.totalLiquidityUsd, 0);
      const pieData = ltvData.map((chain, index) => {
        const percentage = chain.totalLiquidityUsd / totalLiquidity;
        const startAngle = ((index === 0 ? 0 : ltvData.slice(0, index).reduce((acc, prevChain) => acc + prevChain.totalLiquidityUsd / totalLiquidity, 0)) * 1.8 + 0.1) * Math.PI;
        const endAngle = startAngle + percentage * 1.8 * Math.PI;
        return {
          chainId: chain.chainId,
          color: chain.chainId === 'injective-1' ? '#FF6384' : '#36A2EB',
          startAngle,
          endAngle,
        };
      });

      const g = svg.append('g').attr('transform', `translate(${width / 2}, ${height / 2})`);

      g.selectAll('path')
        .data(pieData)
        .enter()
        .append('path')
        .attr('d', arc)
        .style('fill', (d) => d.color);
    }
  }, [chartRef, ltvData]);

  return (
    <div>
      <h3>Liquidity per Deployment</h3>
      <p>This is a sample Liquidity Per Deployment component.</p>
      <svg ref={chartRef} width="200" height="200" viewBox="0 0 200 200"></svg>
    </div>
  );
};

export default LiquidityPerDeployment;
