import React, { useRef, useEffect, useState } from 'react';
import axios from 'axios';
import * as d3 from 'd3';
import astroport from '../../../../assets/StatusReport/astroport.png';
import terra from '../../../../assets/StatusReport/terra.png';
import injective from '../../../../assets/StatusReport/injective.png';

const LiquidityPerDeployment = () => {
  const chartRef = useRef(null);
  const [ltvData, setLtvData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:5001/api/data');
        const data = response?.data?.ltv_data;
        if (data) {
          setLtvData(JSON.parse(data));
        }
      } catch (error) {
        console.error(error);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    if (chartRef.current && ltvData.length) {
      const svg = d3.select(chartRef.current);
      svg.selectAll('*').remove();
      const g = svg.append('g').attr('transform', 'translate(200, 200)');

      const totalLiquidity = ltvData.reduce((acc, chain) => acc + chain.totalLiquidityUsd, 0);

      const arcWidth = 80;
      const radius = 100;
      const arc = d3.arc().outerRadius(radius).innerRadius(radius - arcWidth).cornerRadius(5);

      const pie = d3.pie().value((d) => d.liquidity).startAngle(-0.8 * Math.PI).endAngle(0.8 * Math.PI).padAngle(0.02).sort(null);

      const pieData = ltvData.map((chain) => ({
        chainId: chain.chainId,
        liquidity: chain.totalLiquidityUsd,
        color: chain.chainId === 'injective-1' ? '#83FFCB' : '#534AF9',
      }));

      const arcs = g.selectAll('path.arc').data(pie(pieData)).enter().append('path').attr('class', 'arc');

      arcs
        .attr('fill', (d) => d.data.color)
        .attr('d', arc)
        .on('mouseover', function () {
          d3.select(this).attr('opacity', 0.75);
        })
        .on('mouseout', function () {
          d3.select(this).attr('opacity', 1);
        });

      const labels = g
        .selectAll('text.label')
        .data(pie(pieData))
        .enter()
        .append('text')
        .attr('class', 'label')
        .attr('x', (d) => arc.centroid(d)[0] * 1.5)
        .attr('y', (d) => arc.centroid(d)[1] * 1.5)
        .text((d) => {
          const value = d.data.liquidity >= 1000000 ? `${(d.data.liquidity / 1000000).toFixed(1)}M` : `${(d.data.liquidity / 1000).toFixed(1)}K`;
          return value;
        })
        .attr('text-anchor', 'middle')
        .attr('dominant-baseline', 'central')
        .attr('font-size', '12px')
        .attr('fill', 'white');

      const lines = g
        .selectAll('line.connector')
        .data(pie(pieData))
        .enter()
        .append('line')
        .attr('class', 'connector')
        .attr('x1', (d) => arc.centroid(d)[0] * 1.1)
        .attr('y1', (d) => arc.centroid(d)[1] * 1.1)
        .attr('x2', (d) => arc.centroid(d)[0] * 1.4)
        .attr('y2', (d) => arc.centroid(d)[1] * 1.4)
        .attr('stroke', (d) => d.data.color)
        .attr('stroke-width', '1.5px');

      const imageX = -50;
      const imageY = -50;
      const imageSize = 100;
      g.append('image').attr('xlink:href', astroport).attr('x', imageX).attr('y', imageY).attr('width', imageSize).attr('height', imageSize);

      const iconSize = 40;
      const iconOffset = 20;
      const icons = [
        { href: terra, chainId: 'phoenix-1' },
        { href: injective, chainId: 'injective-1' },
      ];

      icons.forEach((icon) => {
        const associatedData = pieData.find((d) => d.chainId === icon.chainId);
        const position = pie([associatedData])[0];
        const iconCenter = arc.centroid(position);
        g.append('image')
          .attr('xlink:href', icon.href)
          .attr('x', iconCenter[0] - iconSize / 2 + iconOffset)
          .attr('y', iconCenter[1] - iconSize / 2 + iconOffset)
          .attr('width', iconSize)
          .attr('height', iconSize);
      });

      const totalLiquidityText = `Total TVL: ${totalLiquidity >= 1000000 ? `${(totalLiquidity / 1000000).toFixed(1)}M` : `${(totalLiquidity / 1000).toFixed(1)}K`}`;
      g.append('text')
        .attr('x', 0)
        .attr('y', 130)
        .text(totalLiquidityText)
        .attr('text-anchor', 'middle')
        .attr('dominant-baseline', 'central')
        .attr('font-size', '14px')
        .attr('fill', 'white');
    }

}, [chartRef, ltvData]);

return (
    <div>
        <h3>Liquidity per Deployment</h3>
        <p>This is a sample Liquidity Per Deployment component.</p>
        <svg ref={chartRef} width="400" height="400" />
    </div>
);
};

export default LiquidityPerDeployment;
