import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { Doughnut } from 'react-chartjs-2';

const LiquidityPerDeployment = () => {
  const [ltvData, setLtvData] = useState([]);
  const chartRef = useRef(null);

  useEffect(() => {
    const fetchData = async () => {
      const response = await axios.get('http://127.0.0.1:5001/api/data');
      const data = JSON.parse(response.data.ltv_data);
      setLtvData(data);
    };

    fetchData();

    return () => {
      if (chartRef.current) {
        chartRef.current.chartInstance.destroy();
      }
    };
  }, []);

  const chartData = {
    labels: ltvData.map(chain => chain.chainId),
    datasets: [
      {
        data: ltvData.map(chain => chain.totalLiquidityUsd),
        backgroundColor: ltvData.map(chain =>
          chain.chainId === 'injective-1' ? '#FF6384' : '#36A2EB'
        ),
      },
    ],
  };

  const chartOptions = {
    cutout: '75%',
    rotation: Math.PI,
    circumference: Math.PI,
    plugins: {
      legend: {
        display: true,
      },
    },
  };

  return (
    <div>
      <h3>Liquidity per Deployment</h3>
      <p>This is a sample Liquidity Per Deployment component.</p>
      <Doughnut ref={chartRef} data={chartData} options={chartOptions} />
    </div>
  );
};

export default LiquidityPerDeployment;
