import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Top10Pools.css';

const Top10Pools = () => {
  const [pools, setPools] = useState([]);

  useEffect(() => {
    const fetchTopTenPools = async () => {
      try {
        const response = await axios.get('/api/data');
        console.log("Raw response data:", response.data); // Debug line
        const topTenPools = JSON.parse(response.data.top_ten_pools);
        console.log("Parsed top ten pools:", topTenPools); // Debug line
        setPools(topTenPools);
      } catch (error) {
        console.log(error);
      }
    };

    fetchTopTenPools();
  }, []);

  console.log("State:", pools); // Debug line

  const formatLiquidity = (value) => {
    return `${(value / 1000000).toFixed(2)}M`;
  };

  return (
    <div>
      <h2>Top 10 Pools by Liquidity</h2>
      <table>
        <thead>
          <tr>
            <th>Pool Pair</th>
            <th>Network</th>
            <th>Liquidity</th>
            <th>Rewards</th>
            <th>Combined APR</th>
          </tr>
        </thead>
        <tbody>
          {pools.map((pool, index) => (
            <tr key={index}>
              <td>{pool.asset_symbols}</td>
              <td>{pool.chainId}</td>
              <td>{formatLiquidity(pool.poolLiquidityUsd)}</td>
              <td></td>
              <td>{pool.totalRewards.apr}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Top10Pools;
