import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { Chart } from 'chart.js';

const LiquidityPerDeployment = () => {
  const [ltvData, setLtvData] = useState([]);
  const chartRef = useRef(null);

  useEffect(() => {
    const fetchData = async () => {
      const response = await axios.get('http://127.0.0.1:5001/api/data');
      const data = JSON.parse(response.data.ltv_data);
      setLtvData(data);
    };

    fetchData();
  }, []);

  const customAnglePlugin = {
    id: 'customAngle',
    beforeUpdate(chart) {
      if (chart.config.type === 'doughnut') {
        const dataset = chart.data.datasets[0];
        const total = dataset.data.reduce((acc, val) => acc + val, 0);
        const scaleFactor = (Math.PI * 1.6) / total;

        dataset.startAngle = -0.5 * Math.PI;
        dataset.endAngle = dataset.startAngle + scaleFactor * total;
        dataset._meta[0].scaleFactor = scaleFactor;
      }
    },
  };

  useEffect(() => {
    if (ltvData.length > 0 && chartRef.current) {
      const chartData = {
        labels: ltvData.map(chain => chain.chainId),
        datasets: [
          {
            data: ltvData.map(chain => chain.totalLiquidityUsd),
            backgroundColor: ltvData.map(chain =>
              chain.chainId === 'injective-1' ? '#FF6384' : '#36A2EB'
            ),
          },
        ],
      };

      const chartOptions = {
        plugins: {
          customAngle: true,
          legend: {
            display: true,
          },
        },
        cutout: '50%',
      };

      Chart.register(customAnglePlugin);

      new Chart(chartRef.current, {
        type: 'doughnut',
        data: chartData,
        options: chartOptions,
      });
    }
  }, [ltvData]);

  return (
    <div>
      <h3>Liquidity per Deployment</h3>
      <p>This is a sample Liquidity Per Deployment component.</p>
      <canvas ref={chartRef} />
    </div>
  );
};

export default LiquidityPerDeployment;
