import React, { useRef, useEffect } from 'react';
import * as d3 from 'd3';
import terra from '../../../../assets/StatusReport/terra.png';
import injective from '../../../../assets/StatusReport/injective.png';

const LiquidityPerDeployment = () => {
  const chartRef = useRef();

  useEffect(() => {
    const data = [
      { chainId: 'phoenix-1', liquidity: 11400000, color: '#534AF9' },
      { chainId: 'injective-1', liquidity: 5700000, color: '#83FFCB' },
    ];

    const width = 400;
    const height = 400;
    const radius = Math.min(width, height) / 2;
    const arc = d3.arc().innerRadius(radius * 0.5).outerRadius(radius * 0.8).cornerRadius(5);
    const pie = d3.pie().sort(null).value((d) => d.liquidity).startAngle(-Math.PI / 2).endAngle(Math.PI / 2);

    const svg = d3.select(chartRef.current).attr('width', width).attr('height', height).append('g').attr('transform', `translate(${width / 2},${height / 2})`);

    const arcs = svg.selectAll('.arc').data(pie(data)).enter().append('g').attr('class', 'arc');

    arcs
      .append('path')
      .attr('d', arc)
      .attr('fill', (d) => d.data.color)
      .on('mouseover', function (d) {
        d3.select(this).attr('fill', d3.rgb(d.data.color).brighter(0.5));
      })
      .on('mouseout', function (d) {
        d3.select(this).attr('fill', d.data.color);
      });

    const lines = arcs.append('line').attr('x1', (d) => arc.centroid(d)[0] * 0.8).attr('y1', (d) => arc.centroid(d)[1] * 0.8).attr('x2', (d) => arc.centroid(d)[0] * 1.4).attr('y2', (d) => arc.centroid(d)[1] * 1.4).style('stroke', (d) => d.data.color).style('stroke-width', 2);

    const text = arcs
      .append('text')
      .attr('x', (d) => arc.centroid(d)[0] * 1.65)
      .attr('y', (d) => arc.centroid(d)[1] * 1.65)
      .attr('text-anchor', 'middle')
      .text((d) => `${(d.data.liquidity / 1000000).toFixed(1)}M`)
      .style('fill', 'white');

    const logoImages = {
      'phoenix-1': terra,
      'injective-1': injective,
    };

    arcs
      .append('image')
      .attr('xlink:href', (d) => logoImages[d.data.chainId])
      .attr('x', (d) => arc.centroid(d)[0] * 1.25 - 15)
      .attr('y', (d) => arc.centroid(d)[1] * 1.25 - 15)
      .attr('width', 30)
      .attr('height', 30)


      svg.append('image')
      .attr('xlink:href', 'astroport_logo_url_here')
      .attr('x', -30)
      .attr('y', -30)
      .attr('width', 60)
      .attr('height', 60);

    svg.append('text')
      .attr('x', 0)
      .attr('y', 100)
      .attr('text-anchor', 'middle')
      .text('Total TVL')
      .style('font-size', '24px')
      .style('fill', 'white');
  }, []);

  return <svg ref={chartRef}></svg>;
};

export default LiquidityPerDeployment;
