import React, { useRef, useEffect, useState } from 'react';
import axios from 'axios';
import * as d3 from 'd3';
import astroport from '../../../../assets/StatusReport/astroport.png';
import terra from '../../../../assets/StatusReport/terra.png';
import injective from '../../../../assets/StatusReport/injective.png';

const LiquidityPerDeployment = ({ data }) => {
  const chartRef = useRef();

  useEffect(() => {
    const svg = d3.select(chartRef.current);
    svg.selectAll('*').remove();

    const width = 400;
    const height = 400;
    const radius = Math.min(width, height) / 2;

    const color = d3.scaleOrdinal()
      .domain(data.map(d => d.chainId))
      .range(data.map(d => d.color));

    const pie = d3.pie()
      .value(d => d.totalLiquidityUsd)
      .sort(null);

    const arc = d3.arc()
      .innerRadius(radius - 100)
      .outerRadius(radius - 50);

    const g = svg.append('g')
      .attr('transform', `translate(${width / 2}, ${height / 2})`);

    const path = g.selectAll('path')
      .data(pie(data))
      .enter().append('path')
      .attr('d', arc)
      .attr('fill', d => color(d.data.chainId));

    g.selectAll('line')
      .data(pie(data))
      .enter().append('line')
      .attr('x1', (d) => arc.centroid(d)[0] * 1.8)
      .attr('y1', (d) => arc.centroid(d)[1] * 1.8)
      .attr('x2', (d) => arc.centroid(d)[0] * 2.2)
      .attr('y2', (d) => arc.centroid(d)[1] * 2.2)
      .style('stroke', 'white')
      .style('stroke-width', 2);

    g.selectAll('text')
      .data(pie(data))
      .enter().append('text')
      .attr('transform', (d) => `translate(${arc.centroid(d)[0] * 2.5}, ${arc.centroid(d)[1] * 2.5})`)
      .attr('dy', '0.35em')
      .style('text-anchor', 'middle')
      .style('fill', 'white')
      .text((d) => `${d.data.chainId}: ${(d.data.totalLiquidityUsd / 1000000).toFixed(2)}M`);

    g.selectAll('image')
      .data(pie(data))
      .enter().append('image')
      .attr('xlink:href', (d) => (d.data.chainId === 'injective-1' ? injective : terra))
      .attr('transform', (d) => `translate(${arc.centroid(d)[0] * 2.5 - 10}, ${arc.centroid(d)[1] * 2.5 - 10})`)
      .attr('width', 20)
      .attr('height', 20);
  }, [data]);

  return (
    <div>
      <h3>Liquidity per Deployment</h3>
      <svg ref={chartRef} width="400" height="400">
        <image xlinkHref={astroport} x="170" y="170" height="60" width="60" />
      </svg>
    </div>
  );
};

export default LiquidityPerDeployment;
