import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import * as d3 from 'd3';
import astroport from '../../../../assets/StatusReport/astroport.png';

const LiquidityPerDeployment = () => {
  const [ltvData, setLtvData] = useState([]);
  const chartRef = useRef(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:5001/api/data');
        const data = response?.data?.ltv_data;
        if (data) {
          setLtvData(JSON.parse(data));
        }
      } catch (error) {
        console.error(error);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    if (chartRef.current) {
      const svg = d3.select(chartRef.current);
      const width = +svg.attr('width');
      const height = +svg.attr('height');
      const radius = Math.min(width, height) / 2;
      const arcWidth = radius / 5;

      const arc = d3
        .arc()
        .outerRadius(radius - arcWidth)
        .innerRadius(radius - arcWidth * 2)
        .startAngle((d) => d.startAngle)
        .endAngle((d) => d.endAngle)
        .padAngle(0.02)
        .cornerRadius(5);

      const totalLiquidity = ltvData.reduce((acc, chain) => acc + chain.totalLiquidityUsd, 0);
      const pieData = ltvData.map((chain, index) => {
        const percentage = chain.totalLiquidityUsd / totalLiquidity;
        const startAngle = ((index === 0 ? 0 : ltvData.slice(0, index).reduce((acc, prevChain) => acc + prevChain.totalLiquidityUsd / totalLiquidity, 0)) * 1.8 + 0.9) * Math.PI;
        const endAngle = startAngle + percentage * 1.8 * Math.PI;
        return {
          chainId: chain.chainId,
          color: chain.chainId === 'injective-1' ? '#FF6384' : '#36A2EB',
          startAngle,
          endAngle,
          liquidity: chain.totalLiquidityUsd,
        };
      });

      const g = svg.append('g').attr('transform', `translate(${width / 2}, ${height / 2})`);

      const paths = g
        .selectAll('path')
        .data(pieData)
        .enter()
        .append('path')
        .attr('d', arc)
        .style('fill', (d) => d.color)
        .on('mouseover', function (event, d) {
          d3.select(this).transition().duration(200).attr('transform', (d) => `translate(${arc.centroid(d)})`);
        })
        .on('mouseout', function (event, d) {
          d3.select(this).transition().duration(200).attr('transform', 'translate(0, 0)');
        });

      const labels = g
        .selectAll('text.label')
        .data(pieData)
        .enter()
        .append('text')
        .attr('class', 'label')
        .attr('x', (d) => arc.centroid(d)[0] * 1.5)
        .attr('y', (d) => arc.centroid(d)[1] * 1.5)
        .text((d) => {
          const value = d.liquidity >= 1000000 ? `${(d.liquidity / 1000000).toFixed(1)}M` : `${(d.liquidity / 1000).toFixed(1)}K`;
          return value;
        })
        .attr('text-anchor', 'middle')
        .attr('dominant-baseline', 'central')
        .attr('font-size', '12px');

      const lines = g
        .selectAll('line.connector')
        .data(pieData)
        .enter()
        .append('line')
        .attr('class', 'connector')
        .attr('x1', (d) => arc.centroid(d)[0])
        .attr('y1', (d) => arc.centroid(d)[1])
        .attr('x2', (d) => arc.centroid(d)[0] * 1.4)
        .attr('y2', (d) => arc.centroid(d)[1] * 1.4)
        .style('stroke', '#333')
        .style('stroke-width', '1px');

      const imgSize = radius - arcWidth * 2;
      const image = g
        .append('image')
        .attr('xlink:href', astroport)
        .attr('x', -imgSize / 2)
        .attr('y', -imgSize / 2)
        .attr('width', imgSize)
        .attr('height', imgSize);
    }
  }, [chartRef, ltvData]);

  return (
    <div>
      <h3>Liquidity per Deployment</h3>
      <p>This is a sample Liquidity Per Deployment component.</p>
      <svg ref={chartRef} width="400" height="400" viewBox="0 0 400 400" />
    </div>
  );
};

export default LiquidityPerDeployment;
